import React, {Component} from 'react'


class AddNew extends Component{

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            comment: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handleChange(event) {
        let someName = document.querySelector('.inputName').value;
        let someComment = document.querySelector('.inputComment').value;
        this.setState({
            name: someName,
            comment: someComment
        });
      }

      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.name + ' ' + this.state.comment);
        event.preventDefault();
        this.setState({
            name: "",
            comment:""
        });
      }


    render(){
        return(
            <div>
                <p>this is the form section</p>

                <form onSubmit={this.handleSubmit}>
                    <label>
                    Name:
                    <input className="inputName" type="text" value={this.state.name} onChange={this.handleChange} />
                    </label>
                    <label>
                    Comment:
                    <input className="inputComment" type="text" value={this.state.comment} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default AddNew;
