import React, {Component} from 'react'

import Stack from '@mui/material/Stack';
import SnackbarContent from '@mui/material/SnackbarContent';



class DisplayInfo extends Component{
    constructor(props){
        super(props)
        this.state = {comments: ''}
    }




    render(){

        const styles = {
            ma:{
                margin: 'auto'
            },
            bg: {
                background: 'white',
                color:'black'
              }
          };



        let stArray = []
        let getData = function(){
            fetch('http://localhost:5000/get')
                    .then(response => response.json())
                    .then((data) => {
                        //console.log(data.dbArray)
                        data.dbArray.forEach((element) => {
                           stArray.push({name:element.name, comment:element.comment})
                    })
                })
                    .catch(error => console.error(error));    
           }()



            // const newNames = [{name:'jesse', comment:'this is jesses comment'}, {name:'holly', comment:'this is hollys comment'}]
            // const showComments = newNames.map((obj) => (
            //     (<SnackbarContent key={obj.name.toString()} style={styles.bg} message={obj.name + obj.comment}/>)
            // ))




            const waitList = () => {
                const showNewComments = stArray.map((nextObj, i) => (
                    (<SnackbarContent key={i.toString()} style={styles.bg} message={nextObj.name + nextObj.comment}/>)
                ))
                this.setState({comments:showNewComments})
            }


            setTimeout(function() {waitList()}, 2000);
            console.log(this.state.comments)



        return(
            <div>                
                <p>this is the return from the api</p>
                <div className="showData">
                    <ul className="theList"></ul>
                </div>
                <Stack spacing={2} sx={{ maxWidth: '80%' }} style={styles.ma}>
                   {/* {showComments} */}
                   {this.state.comments}
                </Stack>

            </div>   
        )
    }
}

export default DisplayInfo;
