import React, {Component} from 'react'

class DisplayInfo extends Component{

    render(){

        const stArray = []
        let getData = function(){

            fetch('http://localhost:5000/get')
                    .then(response => response.json())
                    .then(data => {
                       //console.log(data.dbArray);
                       data.dbArray.map((x) => {
                            //console.log(x)
                            stArray.push(x)
                       })
                       logThis()
                    })
                    .catch(error => console.error(error));

            }()

            let theList
            function logThis(){
                console.log(stArray)
                const listObj = stArray.map((anything) => {
                    <li key={anything.name.toString()}>`the name is ${anything.name} and the comment is: ${anything.comment}`</li>
                    console.log(`the name is ${anything.name} and the comment is: ${anything.comment}`)
                })
                theList = listObj;
                console.log("the list is " + theList)
            }




        return(
            <div>
                <p>this is the return from the api</p>
                <div className="showData">
                    <ul>{theList}</ul>
                </div>
            </div>



        )
    }
}

export default DisplayInfo;
