import React, {Component} from 'react'

import Comment from './Comment';
import SnackbarContent from '@mui/material/SnackbarContent';


class DisplayInfo extends Component{

    render(){

        const stArray = []
        let getData = function(){

            fetch('http://localhost:5000/get')
                    .then(response => response.json())
                    .then(data => {
                       //console.log(data.dbArray);
                       data.dbArray.map((x) => {
                            //console.log(x)
                            stArray.push(x)
                       })
                       logThis();
                    })
                    .catch(error => console.error(error));

            }()

            const logThis = function(){
                const listObj = stArray.map((anything) => {
                    let theName = anything.name;
                    let theComment = anything.comment;
                    let theLI = `<li key={theName.toString()}>name: ${theName}, comment: ${theComment}</li>`
                    const theUL = window.document.querySelector('.theList')
                    theUL.insertAdjacentHTML('afterend', theLI)

                    let name = (<h1>{theName}</h1>)
                    let comment = (<p>{theComment}</p>)
                    let message = (<div>{name}{comment}</div>)
                    let post = (<SnackbarContent style={styles.bg} message={message}/>)
                    //const thePC = window.document.querySelector('.postComment')
                    //thePC.insertAdjacentHTML('afterend', post)
                    //console.log(thePC)
                    return post
                })
            }


            const styles = {
                bg: {
                    background: 'white',
                    color:'black'
                  }
            }




        return(
            <div>                
                <p>this is the return from the api</p>
                <div className="showData">
                    <ul className="theList"></ul>
                </div>
                <Comment>
                {logThis()}
                </Comment>
            </div>   
        )
    }
}

export default DisplayInfo;
