import React, {Component} from 'react';


class Login extends Component{

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handleChange(event) {
        let aUsername = document.querySelector('#username').value;
        let aPassword = document.querySelector('#password').value;
        this.setState({
            username: aUsername,
            password: aPassword
        });
      }

      handleSubmit(event) {
        event.preventDefault();
            console.log(this.state.name + "," + this.state.password)
            fetch('http://localhost:5000/auth', {
              method: 'POST',
              headers: {'Content-Type': 'application/json'},
              // We convert the React state to JSON and send it as the POST body
              body: JSON.stringify({"username": this.state.username, "password": this.state.password})
          }).then(function(response) {
              console.log(response)
              return response.json();
          });

        // let showLI = `<li>name: ${this.state.name},  comment: ${this.state.comment}</li>`
        // let theUL = window.document.querySelector(".theList")
        // theUL.insertAdjacentHTML("afterend", showLI )
        // this.setState({
        //     name: "",
        //     comment:""
        // });

      }



    render(){
        return(
            <div>
                <p>this is the login page</p>

                <div className='login' style={{ backgroundColor: "#435165"}}>
                    <h1>Login</h1>
                    <form onSubmit={this.handleSubmit}>
                        <label for="username">
                            <i className='fas fa-user'></i>
                        </label>
                        <input type='text' name='username' placeholder='Username' id='username' required />
                        <label for="password">
                            <i className='fas fa-lock'></i>
                        </label>
                        <input type='password' name='password' placeholder='Password' id='password' required />
                        <input type='submit' value='Login' />
                    </form>
                </div>

            </div>
        )
    }
}

export default Login;
