import React, {Component} from 'react'


class AddNew extends Component{

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            comment: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handleChange(event) {
        let someName = document.querySelector('.inputName').value;
        let someComment = document.querySelector('.inputComment').value;
        this.setState({
            name: someName,
            comment: someComment
        });
      }

      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.name + ' ' + this.state.comment);
        event.preventDefault();



        this.setState({
            name: "",
            comment:""
        });
      }


      onSubmit = (e) => {
        e.preventDefault();
        // get form data out of state
        const { name, comment } = this.state;

        fetch('http://localhost:5000/post' , {
          method: "POST",
          headers: {
            'Content-type': 'application/json'
          }
          .then((result) => {
            console.log(result)
          })
      })
    }


    render(){
        return(
            <div>
                <p>this is the form section</p>

                <form onSubmit={this.handleSubmit}>
                    <label>
                    Name:
                    <input className="inputName" type="text" value={this.state.name} name="personName" onChange={this.handleChange} />
                    </label>
                    <label>
                    Comment:
                    <input className="inputComment" type="text" value={this.state.comment} name="comment"onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default AddNew;
