import React, {Component} from 'react'

import Comment from './Comment';

import Stack from '@mui/material/Stack';
import SnackbarContent from '@mui/material/SnackbarContent';


class DisplayInfo extends Component{

    render(){

        const styles = {
            ma:{
                margin: 'auto'
            },
            bg: {
                background: 'white',
                color:'black'
              }
          };

        const stArray = []
        let getData = function(){
            fetch('http://localhost:5000/get')
                    .then(response => response.json())
                    .then(data => {
                       //console.log(data.dbArray);
                       data.dbArray.map((x) => {
                            console.log(x)
                            stArray.push(x)
                       })
                       logThis();
                    })
                    .catch(error => console.error(error));

            }()

            const logThis = function(){
                //add the parent Stack element and create the snackbarcontnet inside the loop then add log this to render.
                const listObj = stArray.forEach((anything) => {
                    let theName = anything.name;
                    let theComment = anything.comment;
                    let theLI = `<li key={theName.toString()}>name: ${theName}, comment: ${theComment}</li>`
                    const theUL = window.document.querySelector('.theList')
                    theUL.insertAdjacentHTML('afterend', theLI)
                    console.log(stArray.name)
                })
            }

            let aName;
            let aComment;
            const showComments = stArray.map((obj) => (
                console.log(obj.name)
                (<SnackbarContent key={obj.name.toString()} style={styles.bg} message={obj.name + obj.Comment}/>)
            ))



        return(
            <div>                
                <p>this is the return from the api</p>
                <div className="showData">
                    <ul className="theList"></ul>
                </div>
                <Comment/>

                <Stack spacing={2} sx={{ maxWidth: '80%' }} style={styles.ma}>
                   {showComments}
                </Stack>

            </div>   
        )
    }
}

export default DisplayInfo;
