import React, {Component} from 'react'

import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';

class DisplayInfo extends Component{

    render(){

        const stArray = []
        let getData = function(){

            fetch('http://localhost:5000/get')
                    .then(response => response.json())
                    .then(data => {
                       //console.log(data.dbArray);
                       data.dbArray.map((x) => {
                            //console.log(x)
                            stArray.push(x)
                       })
                       logThis();
                    })
                    .catch(error => console.error(error));

            }()

            const logThis = function(){
                const listObj = stArray.map((anything) => {
                    let theName = anything.name;
                    let theComment = anything.comment;
                    //let theLI = `<li key={theName.toString()}>name: ${theName}, comment: ${theComment}</li>`

                    let newLI = `<li><List sx={{ width: '100%', maxWidth: 760, bgcolor: 'background.paper' }}>
                            <ListItem alignItems="flex-start">
                                <ListItemAvatar>
                                <Avatar alt="Jesse Gray" src="/static/images/avatar/1.jpg" />
                                </ListItemAvatar>
                                <ListItemText
                                primary="Brunch this weekend?"
                                secondary={
                                    <React.Fragment>
                                    <Typography
                                        sx={{ display: 'inline' }}
                                        component="span"
                                        variant="body2"
                                        color="text.primary"
                                    >
                                        Ali Connors
                                    </Typography>
                                    ${theName}
                                    ${theComment}
                                    </React.Fragment>
                                }
                                />
                            </ListItem>
                            <Divider variant="inset" component="li" />
                        </List></li>`

                    // const theUL = window.document.querySelector('.theList')
                    // theUL.insertAdjacentHTML('afterend', newLI)

                    const theUL = window.document.querySelector('.theList')
                    theUL.insertAdjacentHTML('afterend', newLI)
                })
            }





        return(
            <div>

                <p>this is the return from the api</p>
                <div className="showData">
                    <ul className="theList"></ul>
                </div>

            </div>


        )
    }
}

export default DisplayInfo;
